import logo from './logo.svg';
import './App.css';
import Header from './Header';
import {useState} from 'react';

function App() {
  let [first, setFirst] = useState('Home');
  let stateArr = useState('Home');
  let second = stateArr[0];
  let setSecond = stateArr[1];
  console.log(stateArr,second,set)
  let third = 'Home';
  return (
    <>
      <h1>Input value passing from App using State to Header and then from there to Nav</h1>
       <Header first={first} second={second} third={third}/>
       <form>
        <label>First</label>
        <input type='text' onInput={(e) => setFirst(e.target.value)}></input>

        <label>Second</label>
        <input type='text' onInput={(e) => setSecond(e.target.value)}></input>

        <label>Third</label>
        <input type='text' onInput={(e) => third = e.target.value}></input>
       </form>
    </>
  );
}


export default App;
