            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/100?img=17'
        },
        {
            name: 'Jordan',
            rating: '4.5',
            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/100?img=17'
        },
        {
            name: 'Akash',
            rating: '4.8',
            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/100?img=17'
        }
    ]

    return(
        <section className="testimonials">
            <div>

                <RatingCard name={item.name} rating = {item.rating} text = {item.text} img/>
            </div>
        </section>
    )
}

export default Testimonials;
