import { useEffect, useRef, useState } from "react";
import RatingCard from "./RatingCard";

function Testimonials(){

    const reviews = [
        {
            name: 'Williams',
            rating: '4.9',
            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/80?img=49'
        },
        {
            name: 'Jordan',
            rating: '4.5',
            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/80?img=18'
        },
        {
            name: 'Akash',
            rating: '4.8',
            text: 'Little Lemon is a very popular restaurant in our city, I often take meals there, and they never dissapoint me',
            imageURL: 'https://i.pravatar.cc/80?img=17'
        }
    ]

    return(
        <section id="testimonials-section">
            <div>
            <div>
        <h1 style={{color: "black",}}>This week's Specials!</h1>
        <button className="call-to-action">Online Menu</button>
      </div>
                {reviews.map((item) => <RatingCard name={item.name} rating = {item.rating} text = {item.text} imageURL={item.imageURL}/>)}
            </div>
        </section>
    )
}

export default Testimonials;
