import { redirect } from "react-router-dom";

function Bookingform({ availableTimes, occasions }) {
  const handleSubmit = e => {
    e.preventDefault();
    return redirect('/confirmedbooking');
  }
  return (
    <form onSubmit={handleSubmit}>
      <label for="res-date">Choose date</label>
      <input type="date" id="res-date" aria-placeholder="Please enter the date of reservation"/>
      <label for="res-time">Choose time</label>
      <select id="res-time">
        {availableTimes.map((item, i) => (
          <option>{item}</option>
        ))}
      </select>
      <label for="guests">Number of guests</label>
      <input
        type="number"
        min="1"
        max="10"
        id="guests"
        aria-placeholder="Please enter the number of guests"
      />
      <label for="occasion">Occasion</label>
      <select id="occasion">
        {occasions.map((item, i) => (
          <option>{item}</option>
        ))}
      </select>
      <input type="submit" value="Make Your reservation"/>
    </form>
  );
}

export default Bookingform;
