import Bookingform from "./Bookingform";
import { useState, useReducer, useEffect } from "react";
import Main from "./Main";

function ReserveTable() {
  useEffect(() => {
    fetch(
      "https://raw.githubusercontent.com/ahmadrg91/Meta-Front-End-Developer/20251cdeff9c1f73b56ace98ce10b54f28b52383/times.js"
    ).then((response) => console.log(response));
  }, []);

  const updateTimes = (state, action) => {
    alert("hello");
    // eslint-disable-next-line no-undef
    return removeTime("17:00");
  };

  const initializeTimes = () => {
    // eslint-disable-next-line no-undef
    return getTimes(new Date().getDate());
  };

  const [availableTimes, dispatch] = useReducer(
    updateTimes,
    [],
    initializeTimes
  );

  const [occasions, setOccasions] = useState(["Birthday", "Anniversary"]);

  return (
    <>
      <Main>
        <Bookingform
          availableTimes={availableTimes}
          occasions={occasions}
          dispatch={dispatch}
        />
      </Main>
    </>
  );
}

export default ReserveTable;
