import { useRef } from "react";
import { useNavigate } from "react-router-dom";

function Bookingform({ availableTimes, occasions, minusTime }) {
  const navigate = useNavigate();
  const formRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(formRef.current);
    const value = formData.get("res-time");
    minusTime(value);
    navigate("/bookingconfirmed");
  };

  return (
    <form onSubmit={handleSubmit} ref={formRef}>
      <label htmlFor="res-date">Choose date</label>
      <input
        type="date"
        id="res-date"
        aria-placeholder="Please enter the date of reservation"
      />
      <label htmlFor="res-time">Choose time</label>
      <select id="res-time" name="res-time">
        {availableTimes.map((item, i) => (
          <option>{item}</option>
        ))}
      </select>
      <label htmlFor="guests">Number of guests</label>
      <input
        type="number"
        min="1"
        max="10"
        id="guests"
        aria-placeholder="Please enter the number of guests"
      />
      <label htmlFor="occasion">Occasion</label>
      <select id="occasion">
        {occasions.map((item, i) => (
          <option>{item}</option>
        ))}
      </select>
      <input type="submit" value="Make Your reservation" />
    </form>
  );
}

export default Bookingform;
