import Bookingform from "./Bookingform";
import { useState, useReducer, useEffect } from "react";
import Main from "./Main";

function ReserveTable() {
  const updateTimes = (state, action) => {
    return [...state];
  };

  const intializeTimes = () => {
    // eslint-disable-next-line no-undef
    return fetchTimes(new Date().getDate());
  };

  const [availableTimes, dispatch] = useReducer(updateTimes, ["17:00"]);

  const [occasions, setOccasions] = useState(["Birthday", "Anniversary"]);

  return (
    <>
      <Main>
        <Bookingform availableTimes={availableTimes} occasions={occasions} dispatch/>
      </Main>
    </>
  );
}

export default ReserveTable;
